# get movie feedback collection name for a certain user
def get_user_collection(username='username'):
    client = pymongo.MongoClient('localhost', 27017)
    db = client['comp9900moviedb']
    collection_name = 'movie_feedback_'+ username
    return db[collection_name]

# set neutral preference for the movie
def init_feedback(title, year=None, user='username'):
    client = pymongo.MongoClient('localhost', 27017)
    db = client['comp9900moviedb']
    info_collection = db['movie_info']
    feedback_collection = get_user_collection(user)
    if year != None:
        post = info_collection.find_one({'title': title,  'released_year': year,})
        vector = post['vector']
        feedback = {'user': user, 'preference': 'neutral', 'released_year': year, 'title': title, 'tid': post['tid'],'vector': vector, 'rec_para': 0}
        feedback_collection.insert_one(feedback)
    else:
        year = get_movie_info(title)['Year']
        post = info_collection.find_one({'title': title})
        vector = post['vector']
        feedback = {'user': user, 'preference': 'neutral', 'released_year': year, 'title': title, 'tid': post['tid'], 'vector': vector, 'rec_para': 0}
        feedback_collection.insert_one(feedback)

# get personalized recommendation switch status
def get_user_rs_state(user='username'):
    client = pymongo.MongoClient('localhost', 27017)
    db = client['comp9900moviedb']
    rs_state_collection = db['rs_state']
    return rs_state_collection.find_one({'user': user})['state']

# store personalized recommendation switch status
def store_user_rs_state(state, user='username'):
    client = pymongo.MongoClient('localhost', 27017)
    db = client['comp9900moviedb']
    rs_state_collection = db['rs_state']
    rs_state_collection.update_one({'user': user}, {"$set": {'state': state}}, upsert=True)


# check if the movie is recent
def get_year(interval):
    start = interval[0]
    end = interval[1]
    return [e for e in range(start, end+1)]

# cosine similarity
def calculate_cos_sim(x,y):
    x = np.array(x)
    y = np.array(y)
    dot = np.dot(x, y)
    norm_x = np.linalg.norm(x)
    norm_y = np.linalg.norm(y)
    cos_sim = dot / (norm_x * norm_y)
    return cos_sim

# manhattan similarity
def calculate_manhattan_sim(x,y):
    x = np.array(x)
    y = np.array(y)
    manhattan_sim = np.linalg.norm(x - y)
    return manhattan_sim

